import {BUSQUEDA, BUSCARALL, FILTER, GENRES} from './action-types';

const initialState = {
   
    allVideoGamer: [],
    videoGamer:[],
    genres:[]
}

const reducer = (state= initialState, {type, payload})=>{
    console.log("entro al reducer la informacion" + payload );
    
    
    switch( type ){
        case BUSQUEDA:
            return {...state, allVideoGamer: payload}
        case BUSCARALL:
            return {...state, allVideoGamer: payload}
        case FILTER:
            
            if(!state.videoGamer){
                const prevAllVideoGamer = state.allVideoGamer;
                const allCharactersFiltered = state.allVideoGamer.filter(element=> element.genres.includes(payload))
                return{
                    ...state,
                    allVideoGamer:allCharactersFiltered,
                    videoGamer: prevAllVideoGamer
                }  


            }else{
                const prevAllVideoGamer = state.videoGamer;
                const allCharactersFiltered =prevAllVideoGamer.filter(element=> element.genres.includes(payload))
                return{
                    ...state,
                    allVideoGamer:allCharactersFiltered,
                   
                }  

            }
            case GENRES:
                return{
                    ...state, genres:payload
                } 
        default:
            return {...state}
    }




}

export default reducer;
