import  './Card.css';
import  img_mario from '../../img/videogame.png'
import { Link } from "react-router-dom";




const Card = ({data})=>{
    const { id, name,  genres, background_image } = data;
    const urls = (imagen) =>{
        const urlPattern = new RegExp('^(https?:\\/\\/)?' + // Protocolo
        '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|' + // Dominio...
        '((\\d{1,3}\\.){3}\\d{1,3}))' + // ...o direcci칩n IP
        '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*' + // Puerto y ruta
        '(\\?[;&a-z\\d%_.~+=-]*)?' + // Par치metros de consulta
        '(\\#[-a-z\\d_]*)?$', 'i'); // Fragmento de referencia

    if (!urlPattern.test(imagen)) {
        return false;
    }
    }

    //console.log("Elementos de la card "+  name  +  ' '+  genres +  ' '+ background_image );
    return (
        <div className='principal' id={id} >
             <Link to={`/detail/${id}`} >
            <div className="containerCard">
                <img src={
                    urls===true 
                    ?background_image
                    :img_mario 
                    } alt={name} className='imagengame'/>
             <div className='submenuCard'>
                <div className='textocards'>{name}</div>
                <div className='textocards2'>{genres} </div>
               
             </div>
            </div>
            </Link>
        </div>
        
    )
}

export default Card;
