import React, {useState} from 'react';
import './From.css'
import axios from 'axios';

//validaciond de datos

import validation from '../Validation/validation.js'




const Form = ()=>{
    const [errors, setErrors] = useState({});
    const [userData, setUserData]=useState({
        name:'', 
        description:'', 
        platforms:'', 
        image:'', 
        release_date:'', 
        rating:'', 
        genres:''
    });

    const handlChange = (event) =>{
    /*
        setUserData({
            ...userData,
            [event.target.name]:event.target.value
        })

        setErrors(validation({
            ...userData,
            [event.target.name]:event.target.value
        }))
*/
const { name, value } = event.target;

// Validar el campo actual
const fieldError = validation({ [name]: value });

// Establecer el estado de los errores solo para el campo actual
setErrors((prevErrors) => ({
    ...prevErrors,
    [name]: fieldError[name] || '', // Si el campo no tiene error, establecer el mensaje de error como una cadena vacÃ­a
}));

// Actualizar los datos del usuario
setUserData((prevUserData) => ({
    ...prevUserData,
    [name]: value,
}));
    }




const handleGuardar =(event)=>{

    event.preventDefault(); //bloquea el recargar de la pagina 
  

try {
    const endpoint = 'http://localhost:3001/videogames/'
    const {data} =  axios.post(endpoint, userData)
    console.log(data);
} catch (error) {
    console.error('Error al mostrar Juegos:', error);
}



}

const handleLimpiar = () => {
    setUserData({
        name: '',
        description: '',
        platforms: '',
        image: '',
        release_date: '',
        rating: '',
        genres: ''
    });
    setErrors({});
}
   
    return (
    
    <div className="containerCreate">
            <div className="container_titulo"> 
             <h1> Nuevo Videojuego</h1>
            </div>
          
            <form  onSubmit={handleGuardar}>
           <div className="contenedor_texto">
         
          
                Nombre: 
                <input 
                        type="text" 
                        name="name" 
                        value={userData.name}
                        onChange={handlChange}
                        />
                        

                        <br/>
                        {
                                            errors.name && <p className=''>* {errors.name}</p>
                                        }
                DescripciÃ³n: 
                <input 
                type="text" 
                name="description" 
                value={userData.description}
                onChange={handlChange}
                />
                   {
                                            errors.description && <p className=''>* {errors.description}</p>
                                        }
           
              
                <br/>
                Fecha de lanzamiento: 
                <input 
                type="text" 
                name="release_date"
                value={userData.release_date} 
                onChange={handlChange}/>

                                {
                                            errors.release_date && <p className=''>* {errors.release_date}</p>
                                        }


                <br/>
         
                  Rating: 
                  <input 
                  type="text" 
                  name="rating" 
                  value={userData.rating}
                  onChange={handlChange}
                  />
            <br/>
            {
                                            errors.rating && <p className=''>* {errors.rating}</p>
                                        }
            platforms: 
            <input 
            type="text" 
            name="platforms" 
            value={userData.platforms}
            onChange={handlChange}
            />
            <br/>
            {
                                            errors.platforms && <p className=''>* {errors.platforms}</p>
                                        }
            genres: 
            <input 
            type="text" 
            name="genres" 
            value={userData.genres}
            onChange={handlChange}
            />
            <br/>
            {
                                            errors.genres && <p className=''>* {errors.genres}</p>
                                        }

            Imagen: 
            <input 
            type="text" 
            name="image" 
            value={userData.image}
            onChange={handlChange}
            />
            <br/>
            {
                                            errors.image && <p className=''>* {errors.image}</p>
                                        }
            </div>
           
           <div className="container_botones">
            <button className="boton_guardar">Enviar</button> 
            <button className="boton_verificar" onClick={handleLimpiar}>Verificar</button> 

           </div>
           </form>

    </div>)
}

export default Form;
