import {BUSQUEDA} from './action-types';

const initialState = {
    videoGamer:[],
    allVideoGamer: [],
    test:"test"
}

const reducer = (state= initialState, {type, payload})=>{
    switch( type ){
        case BUSQUEDA:
            return {...state, allVideoGamer: payload.allVideoGamer}
        default:
            return {...state}
    }

}

export default reducer;
