import {BUSQUEDA} from './action-types';

const initialState = {
   
    allVideoGamer: {},
    videoGamer:{},
    test:"test"
}

const reducer = (state= initialState, {type, payload})=>{
    
    
    switch( type ){
        case BUSQUEDA:
            return {...state, allVideoGamer: payload.results}
        default:
            return {...state}
    }




}

export default reducer;
