import React from 'react';
import Card from '../Card/Card.js';
import './Cards.css'

import { useSelector } from 'react-redux';


const Cards = ({ startIndex })=>{
   
    const busqueda = useSelector((state) => state.allVideoGamer);
    const [paginationStartIndex, setPaginationStartIndex] = useState(startIndex);

    // Si endIndex supera la longitud de la lista, se establece en el máximo valor permitido
    const endIndex = Math.min(paginationStartIndex + 15, busqueda.length);

    // Función para reiniciar la paginación cuando se llega al final de la lista
    useEffect(() => {
        if (paginationStartIndex >= busqueda.length) {
            setPaginationStartIndex(0);
        }
    }, [paginationStartIndex, busqueda.length]);



    return (
        <div className='contenedor'>
            {
                endIndex > busqueda.length
                ? 
                startIndex= 0
                :null
            }
            {
                endIndex > busqueda.lenght 
                ? 
                    endIndex = 15
                :null
            }

           {
                    
                    
                    
                    busqueda.slice(paginationStartIndex, endIndex).map((element, index)=> ( 
                    
                    <Card key={index} data={element} 
                    
                    />))
       
                
            }

        </div>
    )
}

export default Cards;
