import {BUSQUEDA, BUSCARALL, FILTER, GENRES} from './action-types';

const initialState = {
   
    allVideoGamer: [],
    videoGamer:{},
    genres:[]
}

const reducer = (state= initialState, {type, payload})=>{
    console.log("entro al reducer la informacion" + payload );
    
    
    switch( type ){
        case BUSQUEDA:
            return {...state, allVideoGamer: payload}
        case BUSCARALL:
            return {...state, allVideoGamer: payload}
        case FILTER:
            console.log("state de"+ state.allVideoGamer);
            const allCharactersFiltered = state.allVideoGamer.filter(element=> element.genres === payload)
            return{
                ...state,
                allVideoGamer:allCharactersFiltered
            }
            case GENRES:
                return{
                    ...state, genres:payload
                } 
        default:
            return {...state}
    }




}

export default reducer;
