import React, {useState} from 'react';
import './From.css'
import axios from 'axios';

//validaciond de datos

import validation from '../Validation/validation.js'




const Form = ()=>{
    const [errors, setErros] = useState({});
    const [userData, setUserData]=useState({
        name:'', 
        description:'', 
        platforms:'', 
        image:'', 
        release_date:'', 
        rating:'', 
        genres:''
    });

    const handlChange = (event) =>{
        setUserData({
            ...userData,
            [event.target.name]:event.target.value
        })

        setErros(validation({
            ...userData,
            [event.target.name]:event.target.value
        }))

    }







    
const handleGuardar =async (event)=>{
    event.preventDefault(); //bloquea el recargar de la pagina 

try {
    const endpoint = 'http://localhost:3001/videogames/'
    const {data} = await axios.post(endpoint)
    console.log(data);
} catch (error) {
    console.error('Error al mostrar Juegos:', error);
}




}

const handleLimpiar = ()=>{
    alert("Limpiar ");
}
   
    return (
    
    <div className="containerCreate">
            <div className="container_titulo"> 
             <h1> Nuevo Videojuego</h1>
            </div>
          
            <form  onSubmit={handleGuardar}>
           <div className="contenedor_texto">
         
          
                Nombre: 
                <input 
                        type="text" 
                       
                        name="name" 
                        value={userData.name}
                        onChange={handlChange}
                        />
                        

                        <br/>
                        {
                                            errors.name && <p className=''>* {errors.name}</p>
                                        }
                Descripci√≥n: 
                <input 
                type="text" 
               
                name="description" 
                value={userData.description}
                onChange={handlChange}
                />
           
              
                <br/>
                Fecha de lanzamiento: 
                <input 
                type="text" 
                
                name="release_date"
                value={userData.release_date} 
                onChange={handlChange}/>
                <br/>
         
                  Rating: 
                  <input 
                  type="text" 
                  
                  name="rating" 
                  value={userData.rating}
                  onChange={handlChange}
                  />
            <br/>
            Imagen: 
            <input 
            type="text" 
          
            name="image" 
            value={userData.image}
            onChange={handlChange}
            />
            </div>
           
           <div className="container_botones">
            <button className="boton_guardar" onClick={handleGuardar}>Enviar</button> 
            <button className="boton_verificar" onClick={handleLimpiar}>Verificar</button> 

           </div>
           </form>

    </div>)
}

export default Form;
