import React, {useEffect, useState} from 'react';
import './App.css';
import Loading from './components/Loading/Loading.js';
import {  Routes,Route, useLocation} from "react-router-dom";

import SearchBar from './components/SearchBar/SearchBar.js';
import  Form from './components/From/Form.js';
import Detail from './components/Detail/Detail.js';
import About from './components/About/About.js';
//import Cards from './components/Cards/Cards.js';

import axios from 'axios';
import Home from './components/Home/Home.js';



import { useDispatch } from 'react-redux';
import { Listado } from './Redux/actions.js';

function App() {

  const location = useLocation()
  const [access, setAccess] = useState(false);
  const [busqueda, setBusqueda] = useState([]);
  const dispatch = useDispatch();
  //const [algamerBD, setAllgamerBD] = useState([]);

 /* useEffect(() => {
  
    const fetchData = async () => {
      try {
       
        const endpoint = 'http://localhost:3001/videogames/';
        const response = await axios.get(endpoint);
        console.log("Desarrolló el data");
        const { results } = response.data.allgamersonline;
       // const {result2}= response.data.allgamers
       
       
        const newData =  results.map(element => {
        console.log("datos "+element.id +' - '+element.name);                  
          return {
                              id: element.id,
                              name: element.name,
                              background_image: element.background_image,
                              rating: element.rating,
                              genres: element.genres.name,                           
                            }
                          });
                          setBusqueda((prevBusqueda) => [prevBusqueda, newData]);

                       //  console.log("REvisando la variable busqueda "+ busqueda.name);
                     //  setAllgamerBD((prevBusqueda)=>[prevBusqueda, result2])
                          setAccess(true);
                        
                         
                         
                } catch (error) {
        console.error('Error al mostrar Juegos:', error);
      }
    };
    fetchData();
    
  }, []);*/
  useEffect(() => {
    dispatch(Listado());
  }, [dispatch]);
  
  return (
    <div className="App">
       <div className='container'>
   
        {

         access===true && location.pathname !== '/' 
          ?
          <SearchBar />    
          : null
        }
       
      <Routes>
              <Route path='/' element={<Loading/>}/>
              <Route path='/home' element={<Home busqueda={busqueda}/>}/>
              <Route path='/detail' element={<Detail/>}/>
              <Route path='/form' element={<Form/>}/>
              <Route path='/about' element={<About/>}/>
      </Routes>
      </div>
    </div>

    

  );
}

export default App;
