      console.log('Encontro ' +results[2].name);
    } catch (error) {
      console.log("Error en la busqueda del servicio " + error)
    }
  }
*/

  return (
    <div className="App">
       <div className='container'>

        {
          location.pathname !== '/' 
          ? <SearchBar juegos={juegosall}/>
          : null
        }
       
      <Routes>
              <Route path='/' element={<Loading/>}/>
              <Route path='/home' element={<Home />}/>
              <Route path='/detail' element={<Detail/>}/>
              <Route path='/form' element={<Form/>}/>
              <Route path='/about' element={<About/>}/>
      </Routes>
      </div>
    </div>

    

  );
}

export default App;
