import {useState, useEffect} from 'react'
import {Listado} from '../../Redux/actions.js'
import {connect} from 'react-redux'
import  './Card.css';
import  img_mario from '../../img/videogame.png'





const Card = ({character, allVideoGamer})=>{
const name ="super mario";
const genres = "Ficcion"
    return (
        <div className='principal'>
            <div className="containerCard">
            <img src={img_mario} alt={name} className='imagengame'/>
            <div className='submenuCard'>
                <div>{name}</div>
                <div>{genres}</div>
            </div>
        </div>
        </div>
        
    )
}

const mapStateToProps = (state)=>{
    return {
        allVideoGamer: state.allVideoGamer
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Card);
