import axios from 'axios';

//datos para el dispach




const Form = ()=>{

    const [userData, setUserData]=useState({
        email:'',
        password:'',
        userId:null,
    });
    
const handleGuardar =async (event)=>{
 event.preventDefault();

try {
    const endpoint = 'http://localhost:3001/videogames/'
    const {data} = await axios.post(endpoint)
    console.log(data);
} catch (error) {
    console.error('Error al mostrar Juegos:', error);
}




}

const handleLimpiar = ()=>{
    alert("Limpiar ");
}
   
    return (
    
    <div className="containerCreate">
            <div className="container_titulo"> 
             <h1> Nuevo Videojuego</h1>
            </div>
          
            <form  onSubmit={handleGuardar}>
           <div className="contenedor_texto">
         
          
                Nombre:  <input type="text" id="name" name="name" />
                <br/>
                Descripci√≥n: <input type="text" id="name" name="name" />
           
              
                <br/>
                Fecha de lanzamiento: <input type="text" id="name" name="name" />
                <br/>
         
                  Rating: <input type="text" id="name" name="name" />
            <br/>
            Imagen: <input type="text" id="name" name="name" />
            </div>
           
           <div className="container_botones">
            <button className="boton_guardar" onClick={handleGuardar}>Enviar</button> 
            <button className="boton_verificar" onClick={handleLimpiar}>Verificar</button> 

           </div>
           </form>

    </div>)
}

export default Form;
